import axios from 'axios'

const Login = () => {
  let navigate = useNavigate();

  const[user,setUser]=useState({cust_name:'',cust_password:''})

  const handleChange=(e)=>{
    setUser({...user,[e.target.name]:e.target.value});
  }


  const submitForm = (e) => {
    e.preventDefault();
    const sendData = {
      cust_name: data.cust_name,
      cust_password: data.cust_password,
     
    }

    console.log(sendData);
    axios.post('http://localhost:3300/addcustomer', sendData)
      .then((result) => {
        if (result.data.Status === 'Invalid'){
          alert('invalid User');}
        else{
          nav('/');
          alert("Registration Successful");}
      })
  }


  return (
    <div className="main-box">
      <div className="row">
        <div className="col-md-12 text-center"><h1>Login</h1></div>
      </div>

      <div className="row">
        <div className="col-md-6">Username:</div>
        <div className="col-md-6">
          <input type="username" name="username" required
          onChange={handleChange} value={user.cust_name}/>
        </div>
      </div>

      <div className="row">
        <div className="col-md-6">Password:</div>
        <div className="col-md-6">
          <input type="password" name="password" required
          onChange={handleChange} value={user.cust_user}/>
        </div>
      </div>

      <div className="row">
        <div className="col-md-12 text-center">
          <input type="submit" className="btn btn-success" value="Login" />
        </div>
      </div>
    </div>
  )
}

export default Login;
