import React, { useState, useEffect } from "react";
import axios from "axios";
//import "mdb-react-ui-kit/dist/css/mdb.min.css";
import {
  MDBTable,
  MDBTableHead,
  MDBTableBody,
  MDBRow,
  MDBCol,
  MDBContainer,
  MDBBtn,
  MDBBtnGroup,
} from "mdb-react-ui-kit";

const Customer = () => {
  const [data, setData] = useState([]);
  const[value,setValue] = useState("");

  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    return await axios
      .get("http://localhost:3300/allproperty")
      .then((response) => setData(response.data))
      .catch((err) => console.log(err));
  };

  console.log("data", data);

  const handleReset = () => {}
  const handleSearch = async(e) => {
    e.preventDefault();
    return await axios.get("http://localhost:3300/allproperty?q=${value}")
    .then(respon)
  }

  return (
    <MDBContainer>
      <form
        style={{
          margin: "auto",
          padding: "15px",
          maxWidth: "400px",
          alignContent: "center",
        }}
        className ="d-flex input-group w-auto" 
        onSubmit={handleSearch}
      >
        <input type="text" classname="form-control" placeholder="Search By City..."
        value={value}
        onChange={(e) => setValue(e.target.value)}
        />
        {/* <MDBBtnGroup> */}
        <MDBBtn type="submit" color="dark"  >Search</MDBBtn>
        <MDBBtn class="btn btn-warning" color="info" onClick={() => handleReset()}>Reset</MDBBtn>
        {/* </MDBBtnGroup> */}
      </form>
      <div style={{ margin: "100px" }}>
        <h2 className="text-center">Properties Available</h2>
        <MDBRow>
          <MDBCol size="12">
            <MDBTable>
              <MDBTableHead dark>
                <tr>
                  <th scope="col">Serial No.</th>
                  <th scope="col">Property ID</th>
                  <th scope="col">Property Type</th>
                  <th scope="col">Price</th>
                  <th scope="col">Offer</th>
                  <th scope="col">City</th>
                </tr>
              </MDBTableHead>
              {data.length === 0 ? (
                <MDBTableBody>
                  <tr>
                    <td colSpan={8} className="text-center mb-0">
                      No Available Property
                    </td>
                  </tr>
                </MDBTableBody>
              ) : (
                data.map((item, index) => (
                  <MDBTableBody key={index}>
                    <tr>
                      <th scope="row">{index + 1}</th>
                      <td>{item.prop_id}</td>
                      <td>{item.prop_type}</td>
                      <td>{item.price}</td>
                      <td>{item.offer_type}</td>
                      <td>{item.city}</td>
                    </tr>
                  </MDBTableBody>
                ))
              )}
            </MDBTable>
          </MDBCol>
        </MDBRow>
      </div>
    </MDBContainer>
  );
};

export default Customer;
